class Solution {
public:
    void whatDi(int &ans, int base, int rest, int final_len, int k, int start) {
        if (k-- == 1) return; 
        int i;
        for (i = start;; ++i) {
            int v = base + min(final_len, rest);
            if (v >= k) break;
            rest = max(0, rest-final_len);
            k -= v;   
        }
        ans = ans * 10 + i;
        whatDi(ans, base/10, rest, final_len/10, k, 0);
    }


    int findKthNumber(int n, int k) {
        int base = 1, final_len = 1;
        while (n / base >= 9) {
            base *= 10; base += 1; final_len *= 10;
        } 
        if (n != 9 * base) base /= 10;
        int rest = n - 9 * base;

        int ans = 0;
        whatDi(ans, base, rest, final_len, k+1, 1);
        return ans;
    }
};

