class Solution {
    public int findKthNumber(int n, int k) {
        if(k == 0)
            return 0;
        int curr = 1;
        k = k-1;
        while(k > 0){
            int steps = findSteps(curr, curr+1, n);
            if(steps <= k){
                curr++;
                k = k-steps;
            }
            else{
                curr *= 10;
                k--;
            }
        }
        return curr;
    }

    private int findSteps(long left, long right, int n){
        int result = 0;
        while(left <= n || right <= n){
            result += (Math.min(n+1, right) -left);
            left *= 10;
            right *= 10;
        }
        return result;
    }
}
