public int findKthNumber(int n, int k) {
        long curr = 1;
        k--; // start with 1, skip 1;
        while (k > 0) {
            long step = getStep(n, curr, curr + 1);
            if (step <= k) {
                curr += 1;
                k -= step;
            } else {
                curr *= 10;
                k -= 1;
            }
        }
        return (int) curr;
    }

    private long getStep(int n, long n1, long n2) {
        long step = 0;
        while (n1 <= n) {   // <= 
            step += Math.min(n + 1, n2) - n1; // n + 1, include the parent node;
            n1 *= 10;
            n2 *= 10;
        }
        return step;
    }
