class Solution {
    public int findKthNumber(int n, int k) {
        return findKthNumberWithPrefix(n, k, 1);
    }

    // a helper function taking an additional prefix, this helps us solve the problem recursively.
    public int findKthNumberWithPrefix(int n, int k, int prefix) {
        if(k == 1) {
            return prefix;
        }

        int number_with_prefix_count = (int)numSmallerThanN(n, prefix);
        if(k > number_with_prefix_count) {
            // If all numbers with this prefix smaller or equal to n does not exceed k, check for prefix+1 recursively.
            return findKthNumberWithPrefix(n, k-number_with_prefix_count, prefix+1);
        } else if(prefix*10 <= n) {
            // If all numbers with this prefix smaller or equal to n exceeds k, check for first child recursively.
            return findKthNumberWithPrefix(n, k-1, prefix*10);
        } else {
            // In this case, first child exceeds n, which means we should check for peers instead of children.
            return findKthNumberWithPrefix(n, k-1, prefix+1);
        }
    }

    // a helper function to count all the numbers with specified prefix and smaller or equal to n.
    public long numSmallerThanN(long n, long prefix) {
        long base = 1;
        long result = 0;
        while(prefix * base <= n) {
            if((prefix+1)*base <= n) {
                result += base;
            } else {
                result += n - prefix*base + 1;
            }
            base *= 10;
        }

        return result;
    }
}
