class Solution {
public:
    int findKthNumber(int n, int k) {
        long long cnt=1;
        k--;
        while (k) {
            long long st=0,head=cnt,tail=cnt+1;
            while (head<=n) {
                if (tail>n+1)
                    st += n+1-head;
                else
                    st += tail-head;
                head *= 10;
                tail *= 10;
            }

            if (st<=k) {
                cnt++;
                k-=st;
            }
            else {
                cnt*=10;
                k--;
            }
        }
        return cnt;
    }
};
