func findKthNumber(n int, k int) int {
	if n < k {
		return -1
	}

	cur := 1
	k = k - 1
	for k > 0 {
		subtreesum := getSubTreeSum(n, cur, cur+1)
		if k >= subtreesum {
			cur = cur + 1
			k -= subtreesum
		} else {
			cur = cur * 10
			k -= 1
		}
	}

	return cur
}

func getSubTreeSum(n int, cur int, next int) int {
	sum := 0
	for cur <= n {
		sum += getMin(next-cur, n-cur+1)
		cur = cur * 10
		next = next * 10
	}

	return sum
}

func getMin(a, b int) int {
	if a < b {
		return a
	}

	return b
}
