int num(int n, int pre) { //the amount of the number start with pre and not bigger than n
	int lp = log(pre + 0.5) / log(10) + 1, l = log(n + 0.5) / log(10) + 1;
	int top = n / pow(10, l - lp);
	if (top == pre) {
		return n % (int)pow(10, l - lp) + 1 + (pow(10, l - lp) - 1) / 9; //the length of the number may be l, l + 1...lp
	} else if (top > pre) {
		return (pow(10, l - lp + 1) - 1) / 9;//the length of the number may be l, l + 1...lp
	} else {
		return (pow(10, l - lp) - 1) / 9;//the length of the number may be l, l + 1...lp-1
	}
	return 0;

}

int findKthNumber(int n, int k) {
	int pre = 1; //the ans start with pre
	while (k > 0) {
		if (k == 1) //if the ans is the first one of the number start with pre, return pre itself
			return pre;
		int size = num(n, pre); 
		if (k > size) { //if the ans not start with pre, but start with pre + 1
			k = k - size;
			pre++;
		} else {
			k--; // the ans start with pre, but not the first one of the number start with pre
			pre = pre * 10;
		}
	}
	return 0;
}
