public class Solution {
    public int findKthNumber(int n, int k) {
        long cur = 1;
        if (k == 1) {
            return 1;
        }
        k--;
        for (int i = 1; i <= n; i++) {
            if (cur * 10 <= n) {
                cur = cur * 10;
            } else if (cur % 10 != 9 && cur + 1 <= n) {
                cur++;
            } else {
                while ((cur / 10) % 10 == 9) {
                    cur = cur / 10;
                }
                cur = cur / 10 + 1;
            }
            if (--k == 0) {
                return (int)cur;
            }
        }
        return (int)cur;
    }
}
