                // from index mid to (N - 1) - if even then check left interval
                if(((N - mid) & 1) == 0)
                    right = mid - 1;
                else
                    left = mid + 2;
            }else if(nums[mid] == nums[mid - 1]){
                // from index (mid - 1) to (N - 1) - if even then check left interval
                if(((N - mid + 1) & 1) == 0)
                    right = mid - 2;
                else
                    left = mid + 1;
            }else
                return nums[mid];
        }

        return nums[left];
    }
}
