```cpp
class Solution {
public:
    int singleNonDuplicate(vector<int>& nums) {
        // init possible boundary
        int n = nums.size(), l = 0, r = n - 1;
        while (l < r) {
            // get the middle one
            // for even number of elements, take the lower one
            int m = l + (r - l) / 2;
            // handle case like [3,3,7,7,10,11,11]
             m -= m & 1;
            // exclude m
            if (nums[m] == nums[m + 1]) l = m + 2;
            // include m
            else r = m;
        }
        return nums[l];
    }
};
```

