class Solution {
    public int singleNonDuplicate(int[] nums) {
        if(nums.length == 1) return nums[0];
        //create the search space
        int low = 0;
        int high = nums.length-1;
        while(low <= high){
            //find the middle index;
            int mid = low+(high - low)/2;
            if(mid-1 >=0 && nums[mid] == nums[mid-1]){
                mid--;
                int left_space = mid - low;
                int right_space = high - (mid - 1);
                if(left_space%2 != 0){
                    high = mid - 1;
                }
                else if(right_space%2 != 0){
                    low = mid + 2;
                }
            }
            else if(mid+1 <= nums.length-1 && nums[mid] == nums[mid+1]){
                mid++;
                int left_space = (mid-1) - low;
                int right_space = high - mid;
                if(left_space%2 != 0){
                    high = mid - 2;
                }
                else if(right_space%2 != 0){
                    low = mid + 1;
                }
            }
            else
                return nums[mid];
        }
        return -1;
    }
}
```

