class Solution(object):
    def singleNonDuplicate(self, nums):
        """
        :type nums: List[int]
        :rtype: int
        """
        if len(nums) < 1:
            return None
        l, r = 0, len(nums)-1
        while l < r:
            m = l + (r - l)/2
            if m - 1 < l or m + 1 > r:
                break
            if m % 2 == 0:
                if nums[m] == nums[m+1]:
                    l = m + 2
                else:
                    r = m
            else:
                if nums[m] == nums[m-1]:
                    l = m + 1
                else:
                    r = m
        return nums[l]
