        if(nums[n-2]!=nums[n-1]) // if last element is the answer
            return nums[n-1];
        while(s<=e){
            int m = (s+e)/2 , x = (s-e)/2;
            if(nums[m]!=nums[m+1] && nums[m]!=nums[m-1])
                return nums[m];
            if(x%2==0) // even number of pairs
            {
                if(nums[m-1]==nums[m]) // move left
                    e=m-2;
                else // move right
                    s=m+2;
            }
            else // odd number of pairs 
            {
                if(nums[m-1]==nums[m]) // move right
                    s=m+1;
                else // move left
                    e=m-1;
            }
        }
        return -1; // PLEASE UPVOTE 🥺
    }
};
