class Solution {
    public int singleNonDuplicate(int[] nums) {
        if(nums.length == 1 ) return nums[0];

        if(nums[0] != nums[1]) return nums[0]; // first index
        if(nums[nums.length-1] != nums[nums.length-2]) return nums[nums.length-1]; // last index 

        for (int i = 1; i < nums.length-1; i++) {
            if(!isD(nums,i)){
                return nums[i];
            }
        }

        return -1;

    }
    private static boolean isD(int[] nums, int index){
        return nums[index - 1] == nums[index] || nums[index] == nums[index + 1];
    }
}
