class Solution {
public:
    int singleNonDuplicate(vector<int>& nums) {
        int start = 0, end = nums.size()-1;
        int n = nums.size();

        if(n == 1) return nums[0];

        while(start <= end)
        {
            int mid = start + (end - start)/2;
            // This two if else are for edge conditons 
            if(mid == 0 && nums[mid] != nums[mid + 1])
            {
                return nums[mid];
            }
            else if(mid == n-1 && nums[mid] != nums[n-2])
            {
                return nums[mid];
            }
            // This checks which subarray is having odd length, and move start & end accordingly
            else if(nums[mid] == nums[mid-1])
            {
                int left_sub = (mid - start) + 1;
                int right_sub = (end - mid);

                if(left_sub%2 != 0) end = mid;
                else start = mid + 1;
            }
            else if(nums[mid] == nums[mid+1])
            {
                int right_sub = (end - mid) + 1; 
                int left_sub = mid - start;

                if(right_sub%2 != 0) start = mid;
                else end = mid - 1;
            }
            else return nums[mid];
        }

        return -1;
    }
};
```

