class Solution {
    public int singleNonDuplicate(int[] nums) {
        int n = nums.length;
        if(n == 1) return nums[0];

        int low=0,high=n-1,mid;
        while(low <= high)
        {
			// To avoid Integer overflow
            mid = low + (high-low)/2;

            // to handle edge cases
            if(mid == 0)
            {
                if(nums[mid] != nums[mid+1]) return nums[mid];
            }
            if(mid == n-1)
            {
                if(nums[mid] != nums[mid-1]) return nums[mid];
            }

			// Checking if mid element is single element by comparing it with neighbour elements
            if (nums[mid] != nums[mid-1] && nums[mid] != nums[mid+1]) return nums[mid];
            else // i.e. either element is equal to its prev or next element 
            {
                if(nums[mid] == nums[mid-1])
                {
                    if(mid % 2 == 0) high = mid-1; 
                    else low = mid + 1;
                }
                else // here nums[mid]  = nums[mid+1]
                {
                    if(mid % 2 == 0) low = mid + 1;
                    else high = mid - 1;
                }
            }
        }
        return -1;
    }
}
