// Naive Sol : Just take XoR of the array values and the result will be the single occurring element. 
// Time : O(N) :: Aux_Space : O(1)

class Solution {
public:
    int singleNonDuplicate(vector<int>& nums) {

        int n = nums.size();

        int var = nums[0];

        for(int i=1;i<n;i++){
            var ^= nums[i];
        }
        return var;
    }
};

// Effective Sol : before the single element the last occurence of the element will be @ odd index, but after a single element the last occurrence of the element 
// will be at even index and we just need to find that break point.

// Time : O(Logn) :: Aux_Space : O(1)

class Solution {
public:
    int singleNonDuplicate(vector<int>& nums) {

        int n = nums.size();

        int low= 0,high= n-2; // The high is @ n-2 cuz think the case when the single element is the last element of the array

        while(low<=high){

            int mid = (low+high) >> 1;

            if(nums[mid] == nums[mid^1]){ // [mid^1] will take you to the next odd or even index of the element, and it'll help to know if you are on the left half or not
                low = mid+1;
            }else{
                high = mid-1;
            }

        }
        return nums[low];

    }
};

