        else if(nums[n-1] != nums[n-2]) return nums[n-1];

        int start = 0, end = n-1;
        while(start <= end){
            int mid = start + (end-start)/2;

            if(nums[mid] != nums[mid-1] && nums[mid] != nums[mid+1]) return nums[mid];

