var singleNonDuplicate = function(arr) {

  const binary = (start,end) => {
    if(start == end) { return arr[start]} //BASE CASE

    let  mid = start + Math.floor((end-start)/2);//FIND MID

    //IF MID IS UNIQUE
    if(( arr[mid] != arr[mid-1] )&& (arr[mid] != arr[mid+1])) { return arr[mid] }

    //FIND LEFT AND RIGHT ARRAR LENGTH SEPERATED BY MID  
    let l,r;

    if(arr[mid-1] == arr[mid]){ // IF THE ELEMENT LEFT TO MID IS SAME AS MID
      l = mid-start-1;// -1 FOR COMPUTING THE LNGTH REMOVING THE SAME ELEMENT AS MID 
      r = end-mid
    }else{// THE RIGHT ELEMNT TO MID IS SAME AS MID
      l = mid-start; 
      r = end-mid-1;// IGNORE THE SAME ELEMENT AND COMPUTE THE LENGTH SO -1
      }


    if(l%2 != 0){// IF LEFT LENGTH IS ODD
      if(arr[mid-1] ==arr[mid]){ // AND THE LEFT TO MID IS SAME AS MID 
        return binary(start,mid-2)// APPLY SEARCH ON THE LEFT REMOVING THE SAME ELEMNT
        }
      else{// LEFT TO MID IS NOT SAME AS MID SO NO NEED TO REMOVE ANY THING
          return binary(start, (mid-1));// JUST APPLY APPLY SEARCH TO LEFT 
        }
