class Solution {
public:
    int singleNonDuplicate(vector<int>& nums) {
        int n = nums.size();
        if (n == 1) {
            return nums[0];
        }

        // Handle edge cases separately to avoid complexity
        if (nums[0] != nums[1]) {
            return nums[0];
        }
        if (nums[n - 1] != nums[n - 2]) {
            return nums[n - 1];
        }

        int low = 1;
        int high = n - 2;
        while (low <= high) {
            int mid = low + (high - low) / 2;

            // Check if nums[mid] is the unique element
            if (nums[mid] != nums[mid + 1] && nums[mid] != nums[mid - 1]) {
                return nums[mid];
            }

            // Eliminate the left or right half based on the conditions
            if (mid % 2 == 1 && nums[mid - 1] == nums[mid]) {
                low = mid + 1;
            }
            else if (mid % 2 == 0 && nums[mid - 1] == nums[mid]) {
                high = mid - 1;
            }

        }

        return -1;  // No unique element found
    }
};
