    for(int row = 0; row < matrixSize; row++) {
        int beg = 0;
        int end = matrixSize - 1;
        while(beg < end) {
            int tmp = matrix[row][beg];
            matrix[row][beg] = matrix[row][end];
            matrix[row][end] = tmp;
