/*
Variant II - Rotate Square matrix by 90 deg anti-clockwise // 270deg clockwise

1,2,3           3,6,9
4,5,6   =====>  2,5,8
7,8,9           1,4,7
*/
function rotateSqMatrixAntiClockWise(matrix) {
    // Transpose the matrix
    transposeMatrix(matrix);
    // reverse matrix rows
    reverseMatrixRows(matrix);
};

/*

Variant III - Rotate Square matrix by 180 deg

1,2,3           9,8,7
4,5,6   =====>  6,5,4
7,8,9           3,2,1

*/

function rotateSqMatrixby180Deg(matrix){
    // reverse matrix columns
    reverseMatrixCols(matrix);
    // reverse matrix rows
    reverseMatrixRows(matrix);
}

/*

Variant IV - Rotate Rectangular matrix by by 90 deg clockwise (can't be done in place)

 1, 2, 3, 4            9, 5, 1
 5, 6, 7, 8   =====>  10, 6, 2
 9,10,11,12           11, 7, 3
                      12, 8, 4
*/

function roateMatrixClockWise(matrix){
    let m = matrix.length, n = matrix[0].length;
    // Creating a N X M matrix and initiating each cell to 0.
    let rotatedMatrix = new Array(n).fill(0).map(_ => new Array(m).fill(0));

    for(let i=0; i < m; i++){
        for(let j=0; j < n; j++){
            rotatedMatrix[j][m-i-1] = matrix[i][j];
        }
    }

    return rotatedMatrix;
}

