class Solution {
public:
    void rotate(vector<vector<int>>& matrix) {
        int n = matrix.size();

        /*  take diagonal from top left to botton right, 
            * then swap the symmetry 
            * 1 2 3     1 4 7   
            * 4 5 6  => 2 5 8  
            * 7 8 9     3 6 9    
        */

        for(int i=0; i<n; i++) {
            for(int j=i; j<n; j++){
                swap(matrix[i][j], matrix[j][i]);
            }
        }

        /*  now swap 1st element with last of current row. 
            * repeat till middle of current row.
            * 1 4 7     7 4 1 
            * 2 5 8  => 8 5 2
            * 3 6 9     9 6 3
        */
        for(int i=0; i<n; i++) {
            for(int j=0; j<n/2; j++){
                swap(matrix[i][j], matrix[i][n-j-1]);
            }
        }
    }
};
```

