class Solution {
    public void rotate(int[][] matrix) {
        if (matrix.length == 0)
            return;
        for (int i=0;i<matrix.length;i++)
            for (int j=i+1;j<matrix.length;j++)
            {
                matrix[i][j] = matrix[i][j] + matrix[j][i];
                matrix[j][i] = matrix[i][j] - matrix[j][i];
                matrix[i][j] = matrix[i][j] - matrix[j][i];
            }

        int si = 0, ei = matrix.length-1;
        while (si < ei){
            for (int i = 0; i < matrix.length; i++)
            {
                matrix[i][si] = matrix[i][si] + matrix[i][ei];
                matrix[i][ei] = matrix[i][si] - matrix[i][ei];
                matrix[i][si] = matrix[i][si] - matrix[i][ei];
            }
            si++;
            ei--;
        }
        return;
    }
}
