      struct ListNode* sublists_tail[BUFFER_SIZE];
      for (int i = 0; i < BUFFER_SIZE; ++i) {
        sublists[i] = remaining;
        sublists_tail[i] = NULL;
        for (int j = 0; remaining && j < steps; ++j) {
          sublists_tail[i] = remaining;
          remaining = remaining->next;
