    public class Solution {

        //merge two sorted list, return result head
        public ListNode merge(ListNode h1, ListNode h2){
            if(h1 == null){
                return h2;
            }
            if(h2 == null){
                return h1;
            }

            if(h1.val < h2.val){
                h1.next = merge(h1.next, h2);
                return h1;
            }
            else{
                h2.next = merge(h1, h2.next);
                return h2;
