            a.next = merge(a.next, b);
            return a;
        }else{
            b.next = merge(a, b.next);
            return b;
        }

    }

    // ------------------------------

    // Base case:

    if( head == null ){

        // Empty linked list
        return null;

    }else if ( head.next == null ){

        // Linked list with one node only
        return head;
