        if (head == null) { return null; }
        int cnt = 1;
        while (cnt < step && head.next != null) {
            head = head.next;
            cnt++;
        }
        ListNode newHead = head.next;
        head.next = null;
        return newHead;
    }

    public int getLen(ListNode head){
        int cnt = 0;
        while (head != null) {
            cnt++;
            head = head.next;
        }
        return cnt;
    }
