	private ListNode mergeList (ListNode left, ListNode right) {

		ListNode head = new ListNode ();
		ListNode tail = head;

		while (left != null && right != null) {
			if (left.val < right.val) {
				tail.next = left;
				left = left.next;
			}
			else {
				tail.next = right;
				right = right.next;
			}
			tail = tail.next;
		}

		tail.next = left != null ? left :right;
		return head.next;
