public ListNode sortList(ListNode head) {
		// If no element or one element list is already sorted so return
        if (head == null || head.next == null) {
            return head;
        }
		// Find middle to divide th list into two parts
        ListNode middle = getMiddleNode(head);
        ListNode nextOfMiddle = middle.next;

        // Separate both lists by breaking the list
        middle.next = null;

		// Recursively sort both parts
        ListNode firstPart = sortList(head);
        ListNode secondPart = sortList(nextOfMiddle);
		// Merge both sorted parts
        return merge(firstPart, secondPart);
    }

    private ListNode merge(ListNode l1, ListNode l2) {
        if (l1 == null) {
			// list1 is empty so just return list2
            return l2;
        }
        if (l2 == null) {
			// list2 is empty so just return list1
            return l1;
        }
        if (l1.val <= l2.val) {
			// list1 have smaller value so recursively merge the remaining of list1 with list2
			// then just append this merged list after the smaller element
			// and return the list1
            l1.next = merge(l1.next, l2);
            return l1;
        } else {
			// list2 have smaller value so recursively merge the remaining of list2 with list1
			// then just append this merged list after the smaller element 
			// and return the list2
            l2.next = merge(l1, l2.next);
            return l2;
        }
    }

    private ListNode getMiddleNode(ListNode node) {
		// Slow and fast ptr approch to find middle element
        ListNode slow = node;
        ListNode fast = node;
        while (fast.next != null && fast.next.next != null) {
            slow = slow.next;
            fast = fast.next.next;
        }
        return slow;
    }
