```
/**
 * Definition for singly-linked list.
 * struct ListNode {
 *     int val;
 *     ListNode *next;
 *     ListNode() : val(0), next(nullptr) {}
 *     ListNode(int x) : val(x), next(nullptr) {}
 *     ListNode(int x, ListNode *next) : val(x), next(next) {}
 * };
 */
class Solution {
public:
    ListNode* sortList(ListNode* head) {
        vector<int> a;
        ListNode* ptr=head;
        while(ptr!=NULL)
        {
            a.push_back(ptr->val);
            ptr=ptr->next;
        }
        sort(a.begin(),a.end());
        std::vector<int>::iterator itr=a.begin();
        ptr=head;
        while(itr!=a.end())
        {
            ptr->val=*itr;
            ptr=ptr->next;
            itr++;
        }
        return head;
    }
};
