/**
 * Definition for singly-linked list.
 * public class ListNode {
 *     int val;
 *     ListNode next;
 *     ListNode() {}
 *     ListNode(int val) { this.val = val; }
 *     ListNode(int val, ListNode next) { this.val = val; this.next = next; }
 * }
 */
class Solution {

	//findMid : As the name suggest, it returns us the mid of linkedList
    public ListNode findMid(ListNode head) {
        ListNode fast = head.next;
        ListNode slow = head;
        while(fast!=null && fast.next!=null) {
            slow = slow.next;
            fast = fast.next.next;
        }
        return slow;
    }

	//merge :- returns us a sorted linked list in which two sorted linkedlists are merged 
    public ListNode merge(ListNode l1, ListNode l2) {
		if(l1==null) return l2;         // if l1 is null return l2
        if(l2==null) return l1;        // if l2 is null return l1
        ListNode ans = l1;           

		// assign the lowest value node to ans

        while(l2.val<l1.val) {
            ListNode temp = l1;
            l1=l2;
            l2=temp;
            ans=l1;
        }

		//Traversing and rearranging the nodes in sorted order
        while(l1!=null && l2!=null) {
            ListNode temp = null;
            while(l1!=null && l1.val<=l2.val) {    // if value at l1 is smaller
                temp = l1;
                l1=l1.next;
            }   // exchange l1 and l2
            temp.next = l2;
            temp = l1;
            l1=l2;
            l2=temp;
        }
        return ans;
    }

    public ListNode sortList(ListNode head) {
        //base case
        if(head==null || head.next==null) return head;

        //break linkedlist into 2 halves
        ListNode mid = findMid(head);

        ListNode left = head;
        ListNode right = mid.next;
        mid.next=null;

        //recursive calls to sort both the linkedlists
        left = sortList(left);
        right = sortList(right);

        //merge two sorted linked list
        ListNode ans  = merge(left,right);
        return ans;
    }
}
