class Solution {
public:

    // Function to find mid of linked list using two pointers
    ListNode *findMid(ListNode *head){
        // making fast point to head next, it will return first mid node, if there are multiple
        ListNode *fast = head->next;

        ListNode *slow = head;

        while(fast != NULL && fast->next != NULL){
            fast = fast->next->next;
            slow = slow->next;
        }

        return slow;
    }

    //Function to merge two sorted linked list
    ListNode *mergeList(ListNode *left, ListNode *right){
        // if left list is null, return right list simply
        if(left == NULL) return right;

        // if right list is null, return left list simply
        if(right == NULL) return left;

        // making dummy answer node
        ListNode *ans = new ListNode(0);

        // making temp point to ans dummy node for further operations
        ListNode *temp = ans;

        // now main logic of merging two sorted linked list
        while(left != NULL && right != NULL){
            if(left->val < right->val){
                temp->next = left;
                temp = left;
                left = left->next;
            }else{
                temp->next = right;
                temp = right;
                right = right->next;
            }
        }

        // while left is not null but right becomes null
        while(left != NULL){
            temp->next = left;
            temp = left;
            left = left->next;
        }

        // while right is not null but left becomes null
        while(right != NULL){
            temp->next = right;
            temp = right;
            right = right->next;
        }

        // leaving dummy node and moving to actual head of answer
        ans = ans->next;

        // returning ans, which is head of merged list
        return ans;
    }

    // function to sort the linked list
    ListNode* sortList(ListNode* head) {
        // base case
        if(head == NULL || head->next == NULL) return head;

        // finding mid of linked list
        ListNode *mid = findMid(head);

        // After finding mid, there are 2 halves of linked list

        // left half's head is head itself
        ListNode *leftHalfHead = head;

        // right half's head will start from next of mid
        ListNode *rightHalfHead = mid->next;

        // making mid's next point to null will make it a individual list
        mid->next = NULL;

        // recursively sort the two halves
        leftHalfHead = sortList(leftHalfHead);
        rightHalfHead = sortList(rightHalfHead);

        // merge both halves using merge two sorted list logic
        ListNode *result = mergeList(leftHalfHead, rightHalfHead);

        // return the head of merged and sorted resultant list
        return result;
    }
};

```

