class Solution {
public:

    ListNode *mSentinel;

    ListNode* sortList(ListNode* head) {
        mSentinel = new ListNode(0);
        ListNode *aNode = head;
        ListNode *aPrev = NULL;
        while (aNode) {
            aPrev = aNode;
            aNode = aNode->next;
        }
        if (aPrev != NULL) { return helper(head, aPrev); }
        return NULL;
    }

    ListNode* helper(ListNode *pHead, ListNode *pTail) {

        //We will always NULL terminate these list snips.
        pTail->next = NULL;

        if (pHead == pTail) {
            //Single node case...
            return pHead;
        } else if (pHead->next == pTail) {

            //Two-node case...

            if (pHead->val < pTail->val) {
                //Already sorted...
                return pHead;
            } else {
                //Reverse the two nodes...
                pTail->next = pHead;
                pHead->next = NULL;
                return pTail;
            }
        }

        //Do merge sort...
        ListNode *aFast = pHead;
        ListNode *aMid = pHead;
        ListNode *aPrev = NULL;

        while (aFast != NULL && aFast->next != NULL) {
            aPrev = aMid;
            aMid = aMid->next;
            aFast = aFast->next->next;
        }

        ListNode *aHalf1 = helper(pHead, aPrev);
        ListNode *aHalf2 = helper(aMid,  pTail);
        ListNode *aNext = NULL;

        aPrev = mSentinel;
        aPrev->next = NULL;

        #define MH1 aNext = aHalf1->next; aHalf1->next = NULL; aPrev->next = aHalf1; aPrev = aHalf1; aHalf1 = aNext;
        #define MH2 aNext = aHalf2->next; aHalf2->next = NULL; aPrev->next = aHalf2; aPrev = aHalf2; aHalf2 = aNext;

        while (aHalf1 != NULL && aHalf2 != NULL) {
            if (aHalf1->val < aHalf2->val) {
                MH1;
            } else {
