    // Quick Sort
    ListNode* sortList(ListNode* head) {

        if (!head || !head->next) {
            return head;
        }

        ListNode* l;    // left half
        ListNode* r;    // right half
        ListNode* m;    // pivot
        ListNode* lt;
        ListNode* rt;
        ListNode* mt;
        ListNode d1(0); // dummy
        ListNode d2(0);

        // choose pivot and divide
        l = &d1;
        r = &d2;
        m = head;

        // skip pivot
        head = head->next;
        lt = l;
        rt = r;
        mt = m;

        // divide
        while (head) {
            if (head->val < m->val) {
                lt->next = head;
                head = head->next;
                lt = lt->next;
            } else if (head->val > m->val){
                rt->next = head;
                head = head->next;
                rt = rt->next;
            } else {
                mt->next = head;
                head = head->next;
                mt = mt->next;
            }
        }

        // handle tail
        lt->next = nullptr;
        rt->next = nullptr;
        mt->next = nullptr;

        // remove dummy
        l = l->next; 
        r = r->next;

        // recursive step
        l = sortList(l);
        r = sortList(r);

        // merge
        if (!l) {
            head = m;
            mt->next = r;
        } else {
            head = l;
            lt = l;
            while (lt->next) lt = lt->next; // find the tail of left half
            lt->next = m;
            mt->next = r;
        }
        return head;
    }
