private:
    ListNode* findMid(ListNode* head){
        ListNode *slow=head;
        ListNode *fast=head->next;

        while(fast!=NULL && fast->next!=NULL)
        {
            slow=slow->next;
            fast=fast->next->next;

        }
        return slow;
    }

    ListNode * mergelist(ListNode* left,ListNode* right)
    {
        if(left==NULL)
            return right;
        if(right==NULL)
            return left;

        ListNode* ans=new ListNode(-1);
        ListNode*temp=ans;
        while(left!=NULL && right!=NULL)
        {
