class Solution {
private:
    ListNode *getMid(ListNode *head){
        ListNode *slow = head;
        ListNode *fast = head->next;

        while(fast != NULL && fast->next != NULL){
            fast = fast->next->next;
            slow = slow->next;
        }
        return slow;
    }

private:
    ListNode *merge(ListNode *left , ListNode *right){
        if(left == NULL)
            return right;
        if(right == NULL)
            return left;

        ListNode *ans = new ListNode(-1);//dummy node
        ListNode *temp = ans;

        while(left != NULL && right != NULL){
            if(left->val < right->val){
                temp->next = left;
                temp = temp->next;
                left = left->next;
            }
            else{
                temp->next = right;
                temp = temp->next;
                right = right->next;
            }
        }

        while(left != NULL){
            temp->next = left;
            temp = temp->next;
            left = left->next;
        }
        while(right != NULL){
            temp->next = right;
            temp = temp->next;
            right = right->next;
        }

        ans = ans->next;
        return ans;
    }

public:
    ListNode* sortList(ListNode* head) {
        //base case
        if(head == NULL || head->next == NULL)
            return head;

        ListNode *mid = getMid(head);

        //divide the LL into two halves
        ListNode *left = head;
        ListNode *right = mid->next;
        mid->next = NULL;

        //Use recursion to sort the two halves
        left = sortList(left);
        right = sortList(right);

        //merge the two LL
        ListNode *result = merge(left , right);

        return result;
    }
};
