/**
 * Definition for singly-linked list.
 * public class ListNode {
 *     public int val;
 *     public ListNode next;
 *     public ListNode(int val=0, ListNode next=null) {
 *         this.val = val;
 *         this.next = next;
 *     }
 * }
 */
public class Solution {
    List<int> nums;
    public ListNode SortList(ListNode head) {
        nums = new List<int>();
        ListNode temp = head;

        while (temp != null) {
            nums.Add(temp.val);
            temp = temp.next;
        }

        nums.Sort();
        temp = head;
        int counter = 0;

        while (temp != null) {
            temp.val = nums[counter++];
            temp = temp.next;
        }

        return head;
    }
}
