class Solution {
    public List<Integer> findDisappearedNumbers(int[] nums) {
        List<Integer> res=new ArrayList<>();
        for(int i=0;i<nums.length;i++){
           int val=nums[i];
            while(val!=nums[val-1]){
                int tmp=nums[val-1];
                nums[val-1]=val;
                val=tmp;
            }
        }
        for(int i=0;i<nums.length;i++){
            if(i+1!=nums[i]){
                res.add(i+1);
            }
        }
        return res;
    }
}
