from collections import deque
class Solution:
    def canJump(self, nums):
        """
        :type nums: List[int]
        :rtype: bool
        """
        if not nums: return True
        fringe = deque([0])
        seen = set()
        canReachLast = False
        while fringe:
            idx = fringe.popleft()
            if idx == len(nums)-1: canReachLast = True
            seen.add(idx)
            for nextJump in range(1,nums[idx]+1):
                if idx+nextJump not in seen and idx+nextJump < len(nums):
                    fringe.append(idx+nextJump)
        return canReachLast
