        if (dp[curr]) {
            return dp[curr];
        }
        for (int i = nums[curr]; i > 0; i--) {
            if (canJump(nums, curr + i, dp)) {
                dp[curr] = true;
                return dp[curr];
            }
        }
        dp[curr] = false;
        return dp[curr];
    }

    bool canJump(vector<int>& nums) {
        vector <bool> dp(nums.size(), false);
        return canJump(nums, 0, dp);
    }
};
```
