class Solution {

    public boolean canJump(int[] nums) {
        int n = nums.length;
        boolean[] dp = new boolean[n];
        //base
        dp[0] =true;
        for(int e= 1; e<n;e++) {
            for(int v= e-1;v>=0;v--) {
                int v_reach = v+nums[v];
                if(e<=v_reach && dp[v]) {
