var canJump = function(nums) {
    const memo = new Map();

    function permute(idx) {
        if(memo.has(idx)) return memo.get(idx)
        if(idx >= nums.length-1) return true;
        if(nums[idx] === 0) return false;

        for(let i = nums[idx]; i >= 1; i--) {
            if(permute(idx + i)) {
                memo.set(idx, true);
                return true
            }
        }
        memo.set(idx, false);
        return false;
    }
    return permute(0);
};
```

