class Solution {
    public boolean canJump(int[] nums) {
        int tempIndex = nums.length-1;

        for(int i=nums.length-2;i>=0;i--){
            if(i+nums[i]>=tempIndex){
                tempIndex = i;
            }
        }
        if(tempIndex == 0) return true;
        return false;
    }
}

