            top++; //increment of top by 1

            for(int i=top; i<=bottom; i++) //traversal of last column
            { output.push_back(matrix[i][right]);}

             right--;//decrement of right by 1

            for(int j=right; top<=bottom && j>=left; j--) //traversal of last row
            {output.push_back(matrix[bottom][j]);}

             bottom--;//decrement of bottom by 1

            for(int i=bottom; left<=right && i>=top; i--) //traversal of first column
            {  output.push_back(matrix[i][left]);}

            left++; //increment of left by 1

            // after this the process again repeats..
        }

        return output;
