    public class Solution {
    public List<Integer> spiralOrder(int[][] matrix) {
        List<Integer> list = new ArrayList<Integer>();
        if (matrix.length == 0) return list;

        int m = matrix.length; //width
        int n = matrix[0].length; //height
        int x = 0, y = 0;

        while (m > 0 && n > 0) {
            //If there is only one row or one column, then just put this row/column into the list
            if (m == 1) {
                for (int i = 0; i < n; i++) {
                    list.add(matrix[x][y++]);
                }
                break;
            } else if (n == 1) {
                for (int i = 0; i < m; i++) {
                    list.add(matrix[x++][y]);
                }
                break;
            }

            //left to right
            for (int i = 0; i < n - 1; i++) {
                list.add(matrix[x][y++]);
            } 

            //up to down
            for (int i = 0; i < m - 1; i++) {
                list.add(matrix[x++][y]);
            }

            //right to left
            for (int i = 0; i < n - 1; i++) {
                list.add(matrix[x][y--]);
            } 

            //down to up
            for (int i = 0; i < m - 1; i++) {
                list.add(matrix[x--][y]);
            }

            m = m - 2;
            n = n - 2;
            x++;
            y++;
        }
        return list;
    }
}


