class Solution:
    def spiralOrder(self, matrix: List[List[int]]) -> List[int]:
        res = matrix[0]
        cur_i, cur_j = 0, len(matrix[0]) - 1
        dist_x, dist_y = -(len(matrix[0]) - 1), len(matrix) - 1
        step_x, step_y = -1, 1
        while True:
            if dist_y == 0:
                break
            else:
                for i in range(0, dist_y, step_y):
                    cur_i += step_y
                    res.append(matrix[cur_i][cur_j])
                dist_y = -dist_y + step_y
                step_y *= -1
            if dist_x == 0:
                break
            else:
                for i in range(0, dist_x, step_x):
                    cur_j += step_x
                    res.append(matrix[cur_i][cur_j])
                dist_x = -dist_x + step_x
                step_x *= -1
        return res
