        vector<int> ans;
			int n=matrix.size(),m=matrix[0].size();
        int rs=0,re=n-1,ce=m-1,cs=0;

        while(rs<=re and cs<=ce){
            for(int l=cs;l<=ce;l++){
                ans.push_back(matrix[rs][l]);
            }
            for(int k=rs+1;k<=re;k++){
                ans.push_back(matrix[k][ce]);    
            }
            for(int l=ce-1;l>=cs;l--){
                if(rs!=re) ans.push_back(matrix[re][l]);
            }
            for(int k=re-1;k>rs;k--){
                if(cs!=ce) ans.push_back(matrix[k][cs]);    
            }
            rs++;
            re--;
            ce--;
            cs++;
        }

