            rowBottom--;
            direction = (direction + 1) % 4;
        } else {
            for (int i = rowBottom; i >= rowTop; i--) {
                list.push_back(matrix[i][columnLeft]);
            }
            columnLeft++;
            direction = (direction + 1) % 4;
        }
    }
    return list;
    }
};
