      if (n == 0) break;
      for (var i=0; i<n; i++, y--) list.add(matrix[x][y]);
      x--; y++; n--;

      if (m == 0) break;
      for(var i=0; i<m; i++, x--) list.add(matrix[x][y]);
      y++; x++; m--;
    }
    return list;
  }
